import React, {Component} from "react"
import {Navbar, Nav, NavDropdown, Form, FormControl, Button} from 'react-bootstrap';
import './App.css';
import {BrowserRouter, Switch, Route} from 'react-router-dom';
import {connect} from "react-redux";
import * as actionTypes_home from "./store/actions/home_actions";
import * as actionTypes_auth from "./store/actions/auth_actions";
import PostComponent from "./Components/Posts/PostComponent";
import FormComponent from "./Components/Forms/FormComponent";
import LoginComponent from "./Components/Login/LoginComponent";
import LoginComponent2 from "./Components/Login/LoginComponent2";
import SignupComponent from "./Components/Login/SignupComponent";

class App extends Component<any> {

//constuructor is for creating a state at initialization.
//   constructor(props: any) {
//     super(props);
// }


//use "async" so that componentDidMount() is ran first. Async functions allow promise based processing (functions perfomed in order and after each task is done)          https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Statements/async_function
  async componentDidMount(){
    await this.props.onTryAutoSignup();
    await this.props.fetchData();
    await this.props.awaitData();
  };

  componentDidUpdate(){}


  render(){

    const navBar = (
        //navbar
       <Navbar id="nav" bg="light" expand="lg">
       <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
       <Navbar.Toggle aria-controls="basic-navbar-nav" />
       <Navbar.Collapse id="basic-navbar-nav">
         <Nav className="mr-auto">
           <Nav.Link href="#home">Home</Nav.Link>
           <Nav.Link href="#link">Link</Nav.Link>
           <NavDropdown title="Dropdown" id="basic-nav-dropdown">
             <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
             <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
             <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
             <NavDropdown.Divider />
             <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
           </NavDropdown>
         </Nav>
         <Form inline>
           <FormControl type="text" placeholder="Search" className="mr-sm-2" />
           <Button variant="outline-success">Search</Button>
           {this.props.isAuthenticated ?
           <Button id="login" onClick={this.props.logout()} href="/login2">Logout</Button>
           :
           <Button id="login" href="/login2">Login</Button>
           }
         </Form>

       </Navbar.Collapse>
     </Navbar>
    )

    const home = (

      <div>
        <h1 id="header">Welcome to Music Accelerator!</h1>
          <div className="container">
          {this.props.posts.map((post: any) => {
            return <div className="post_container1" key={post.id}><PostComponent name={post.name} image={post.image} desc={post.desc} likes={post.likes} key={post.id}/></div>    
          })}      
          </div>

        {this.props.isAuthenticated ?
        <FormComponent/> : null }

        {/* end of jsx */}
      </div>
    );


    //if (this.props.data === true){
      return (

        <div className="App">
          <BrowserRouter>

          {/* navBar */}
            {navBar}

          {/* Pages */}
            <Switch>
              <Route exact={true} path="/" render={()=> <div>{home}</div>}/>
              <Route exact={true} path="/login" component={LoginComponent}/>              
              <Route exact={true} path="/login2" component={LoginComponent2}/>
              <Route exact={true} path="/signup" component={SignupComponent}/>
            </Switch>
          </BrowserRouter>
        </div> 
      );
    //}  
  }
}

//directs how the state managed by redux will be mapped to state here. Makes data from store available to component
const mapStateToProps = (state: any) => {
  return {
      text: state.home.text,
      posts: state.home.posts,
      data: state.home.data,
      //checks if user token is null (looged in or not)   if state.auth.token is other than null, it returns true
      isAuthenticated: state.auth.token !== null
  };
};

const mapDispatchToProps = (dispatch: any) => {
  return{
      fetchData: () => dispatch(actionTypes_home.fetchData()),
      awaitData: () => dispatch(actionTypes_home.awaitData()),
      onTryAutoSignup: () => dispatch(actionTypes_auth.authCheckState()),
      logout: () => dispatch(actionTypes_auth.authLogout()),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
