import styled from "styled-components";

export const PostWrapper = styled.div`

    .post_container {
        display:flex;
        justify-content: center;
        flex-direction: column;
        align-items: center;
        align-content: center;
        width: 1000px;
        height: 900px;
        margin-top: 50px;
        margin-bottom: 20px;
    }

    h1 {
        margin: 10px;
    }

    .username {
        font-weight: bold;
    }

    .image {
        width: 600px;
        height: 800px;
    }

    div {
        margin: 10px;
    }

    .button {
        display: flex; 
        margin-bottom: 30px;
    }

    .cardImage{
        height: 10px;
    }

    .actionIcons{
        display: flex;
        align-content: center;
        justify-content: center;
    }
`;
