import React from 'react';
import {connect} from "react-redux";
import {
  Form,
  Input,
  Tooltip,
  Checkbox,
  Button,
} from 'antd';
import { QuestionCircleOutlined } from '@ant-design/icons';
import { registerUser } from '../../store/actions/auth_actions';
import * as actionTypes_auth from "../../store/actions/auth_actions";



const RegistrationForm = (props: any) => {
  const [form] = Form.useForm();

//   const onFinish = (values: any) => {
//     console.log('Received values of form: ', values);
//   };

  const handleSubmit = (e: any) => {
    // e.preventDefault();
    props.form.validateFields((err: any, values: any) => {
        if(!err){
            props.onRegister(
              values.username,
              values.email,
              values.password,
            );

            // props.onAuth(
            //     values.userName, 
            //     values.email,
            //     values.password,
            //     values.confirm);
        }
    })
    //after logged in, redirects you to the "/" page.
    // props.history.push("/");
    }; 

  return (
    <div>
    <Form
      form={form}
      name="register"
      onFinish={handleSubmit}
      initialValues={{
        residence: ['zhejiang', 'hangzhou', 'xihu'],
        prefix: '86',
      }}
      scrollToFirstError
    >

<Form.Item
        name="Username"
        label={
          <span>
            Username&nbsp;
            <Tooltip title="What do you want others to call you?">
              <QuestionCircleOutlined />
            </Tooltip>
          </span>
        }
        rules={[
          {
            required: true,
            message: 'Please input your nickname!',
            whitespace: true,
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        name="email"
        label="E-mail"
        rules={[
          {
            type: 'email',
            message: 'The input is not valid E-mail!',
          },
          {
            required: true,
            message: 'Please input your E-mail!',
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        name="password"
        label="Password"
        rules={[
          {
            required: true,
            message: 'Please input your password!',
          },
        ]}
        hasFeedback
      >
        <Input.Password />
      </Form.Item>

      <Form.Item
        name="confirm"
        label="Confirm Password"
        dependencies={['password']}
        hasFeedback
        rules={[
          {
            required: true,
            message: 'Please confirm your password!',
          },
          ({ getFieldValue }) => ({
            validator(_, value) {
              if (!value || getFieldValue('password') === value) {
                return Promise.resolve();
              }

              return Promise.reject('The two passwords that you entered do not match!');
            },
          }),
        ]}
      >
        <Input.Password />
      </Form.Item>

      {/* <Form.Item
        name="agreement"
        valuePropName="checked"
        rules={[
          {
            validator: (_, value) =>
              value ? Promise.resolve() : Promise.reject('Should accept agreement'),
          },
        ]}

      >
        <Checkbox>
          I have read the <a href="documents">agreement</a>
        </Checkbox>
      </Form.Item> */}
      <Form.Item>
        <Button type="primary" htmlType="submit">
          Register
        </Button>
      </Form.Item>
    </Form>
    </div>
  );
};

const mapStateToProps = (state: any) => {
    return{
        loading: state.auth.loading,
        error: state.error
    }
}

const mapDispatchToProps = (dispatch: any) => {
    return{
      registerUser: (username:any, email:any, password:any) => dispatch(actionTypes_auth.registerUser(username, email, password))
      // onAuth: (username: string, email: string, password1: any, password2: any) => dispatch(actionTypes_auth.authSignup(username, email, password1, password2)),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(RegistrationForm);
