import axios from "axios";

export const AWAIT_DATA = "AWAIT_DATA";
export const DECREMENT = "DECREMENT";
export const ON_MOUNT = "ON_MOUNT";


export const onMount = (posts: string|number) => {
    return {
        type: ON_MOUNT,
        array: posts
    }
}

export const fetchData = () => {
    return (dispatch: any) => {
        axios.get("http://localhost:5000/posts/images").then((response) => {
        let posts: string | number = response.data;
        return posts;
        }).then((posts)=> dispatch(onMount(posts)))
    }
};

export const awaitData = () => {
    return {
        type: AWAIT_DATA,
    }
}
