import React, {useEffect} from "react";
import './PostComponent_style.tsx';
import { PostWrapper } from "./PostComponent_style";
import axios from "axios"


interface Props {
    name?: string;
    image?: string;
    likes?: number;
}

export const PostComponent: React.FC<Props> = (Props) => {

    useEffect(() => {
        axios.get("http://127.0.0.1:8000/posts/posts/").then((response) => {
        let posts: string | number = response.data;
        //putting the content in state
        this.setState({posts: posts})
        })
    });

    return(
        <PostWrapper>
            <div id="container">
                <div id="name">{Props.name}</div>
                <img id="image" src={"./public/" + Props.image} alt=""></img>
                <div id="like">{Props.likes}</div>
            </div>
        </PostWrapper>
    )

}

export default PostComponent;
