import React, {useState} from "react"
import {FormWrapper} from "./FormComponent_style"
import axios from "axios";

interface Props{

}

interface Image{
    onChangeImage: (object: {targetImage: File|null}) => void
}




export const FormComponent: React.FC<Props> = (Props) => {

    const [name, setName] = useState("");
    const [image, setImage] = useState<Image>();
    const [like, setLike] = useState("");

    const postHandler = (e: any) => {
        // e.preventDefault()

        const data = {name, image, like}

        console.log(data)

        axios.post("http://127.0.0.1:8000/posts/posts/", data).then(response => {
          console.log(response.data); 
          })
      }


    return(

    <FormWrapper>
        <div className="container">
            <form className="form" >
                <label>Name:</label>
                    <input
                    type="text"
                    id="input"
                    value={name}
                    onChange={(e)=> setName(e.target.value)}
                    required
                    /><br /><br />
                <label>Image:</label>
                    <input
                    type="file"
                    id="input"
                    onChange={(e: any) => setImage(e.target.files[0])}
                    // required
                    /><br /><br />
                <label>Description:</label>
                    <input
                    type="text"
                    id="input"
                    value={like}
                    onChange={(e)=> setLike(e.target.value)}
                    required
                    /><br /><br />

                    <button id="submit" type="button" className="btn btn-success" onClick={()=> postHandler}>Submit</button>
            </form>
        </div>
    </FormWrapper>
    )

}

export default FormComponent
