import React, {useState} from "react"
import {FormWrapper} from "./FormComponent_style"
import axios from "axios";

interface Props{

}

interface Image{
    onChangeImage: (object: {targetImage: File|null}) => void
}




export const FormComponent: React.FC<Props> = (Props) => {

    const [name, setName] = useState("");
    const [image, setImage] = useState<Image>();
    const [desc, setDesc] = useState("");

    const postHandler = (e: any) => {
        e.preventDefault()

        const data = {name, image, desc}

        console.log(data)

        axios.post("http://127.0.0.1:8000/posts/posts/", data).then(response => {
          console.log(response); 
          }).catch(error => console.log(error))
      }


    return(

    <FormWrapper>
        <div className="container">
            <form className="form" >
                <label>Name:</label>
                    <input
                    type="text"
                    id="input"
                    value={name}
                    onChange={(e)=> setName(e.target.value)}
                    required
                    /><br /><br />
                <label>Image:</label>
                    <input
                    type="image"
                    id="input"
                    onChange={(e: any) => setImage(e.target.files[0])}
                    alt="image"
                    // required
                    /><br /><br />
                <label>Description:</label>
                    <input
                    type="text"
                    id="input"
                    value={desc}
                    onChange={(e)=> setDesc(e.target.value)}
                    required
                    /><br /><br />

                    {/* don`t call the function at the spot, because you won`t get the e and it become asynchronous. onClick passes 
                    the click event to postHandler using the reference. Also dont use onSubmit on a button, it`s for a form, 
                    Just use a callback (reference) to refer to the function defined.   */}
                    <button id="submit" className="btn btn-success" onClick={postHandler}>Submit</button>

            </form>
        </div>
    </FormWrapper>
    )

}

export default FormComponent
