import React, {Component} from "react"
import './App.css';
import {BrowserRouter, Switch, Route} from 'react-router-dom';
import {connect} from "react-redux";
import * as actionTypes_home from "./store/actions/home_actions";
import * as actionTypes_auth from "./store/actions/auth_actions";
import PostComponent from "./Components/Posts/PostComponent";
import FormComponent2 from "./Components/Forms/FormComponent";
// import LoginComponent from "./Components/Login/LoginComponent_test";
import LoginComponent from "./Components/Login/LoginComponent";
import SignupComponent from "./Components/Login/SignupComponent";
import navComponent from "./Components/Navbar/navComponent";

class App extends Component<any> {

//constuructor is for creating a state at initialization.
//   constructor(props: any) {
//     super(props);
// }


//use "async" so that componentDidMount() is ran first. Async functions allow promise based processing (functions perfomed in order and after each task is done)          https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Statements/async_function
  async componentDidMount(){
    // await this.props.onTryAutoSignup();

    await this.props.loadUser();
    await this.props.fetchPosts();
    await this.props.awaitData();
  };

  componentDidUpdate(){}


  render(){

    const home = (

      <div>
        <h1 id="header">Welcome to Music Accelerator!</h1>
          <div className="container">
          {this.props.posts.map((post: any, key: any) => {
            return <PostComponent id= {post._id} username={post.username} filename={post.filename} description={post.description} likes={post.likes} key={post._id}/>   
          })}      
          </div>

        {/* {this.props.isAuthenticated ?
        <FormComponent/> : null } */}

        <FormComponent2/>

        {/* end of jsx */}
      </div>
    );


      return (

        <div className="App">
          <BrowserRouter>
              <Route component={navComponent}/>
          {/* Pages */}
            <Switch>
              <Route exact={true} path="/" render={()=> <div>{home}</div>}/>         
              <Route exact={true} path="/login2" component={LoginComponent}/>
              <Route exact={true} path="/signup" component={SignupComponent}/>
            </Switch>
          </BrowserRouter>
        </div> 
      );
    //}  
  }
}

//directs how the state managed by redux will be mapped to state here. Makes data from store available to component
const mapStateToProps = (state: any) => {
  return {
      text: state.home.text,
      posts: state.home.posts,
      data: state.home.data,
      image: state.home.images,
      //checks if user token is null (looged in or not)   if state.auth.token is other than null, it returns true
      isAuthenticated: state.auth.token !== null
  };
};

const mapDispatchToProps = (dispatch: any) => {
  return{
      fetchPosts: () => dispatch(actionTypes_home.fetchPosts()),
      awaitData: () => dispatch(actionTypes_home.awaitData()),
      loadUser: () => dispatch(actionTypes_auth.loadUser())
      // onTryAutoSignup: () => dispatch(actionTypes_auth.authCheckState()),
      // logout: () => dispatch(actionTypes_auth.authLogout()),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App);


