import React, {Component} from "react"
import PostComponent from "./Components/Posts/PostComponent"
// import FormComponent from "./Components/Form/FormComponent"
import {Navbar, Nav, NavDropdown, Form, FormControl, Button} from 'react-bootstrap';
import axios from "axios";
import './App.css';

class App extends Component {

  constructor(props: any) {
    super(props);

    // this.onChangeName = this.onChangeName.bind(this);
    // this.onChangeEmail = this.onChange.bind(this);
    // this.onChangePhone = this.onChangePhone.bind(this);
    // this.onSubmit = this.onSubmit.bind(this);

    this.state = {
        posts: [],
        input: {
          name: '',
          image: "",
          desc: ''
        }
    }
}


  state = {
    posts: [],
    input: {
    name: "",
    image: "",
    desc:""
    }
  }

  componentDidMount(){
    axios.get("http://127.0.0.1:8000/posts/posts/").then((response) => {
      let posts: string | number = response.data;
      //putting the content in state
      this.setState({posts: posts})
    })
  }


  //Form Functions
  componentDidUpdate(){
    // console.log(this.state.posts)
  }

  nameChangeHandler = (e) => {
    this.setState({ name: e.target.value })
  }

  imageChangeHandler = (e) => {
    this.setState({ image: e.target.value })
  }

  descChangeHandler = (e) => {
    this.setState({ desc: e.target.value })
  }

  postHandler = (e) => {
    e.preventDefault()

    const data = this.state.input;

    console.log(data)

    axios.post("http://127.0.0.1:8000/posts/posts/", data).then(response => {
      console.log(response); 
      })
  }

  render(){
    return (
      <div className="App">

        <Navbar id="nav" bg="light" expand="lg">
          <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#link">Link</Nav.Link>
              <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
              </NavDropdown>
            </Nav>
            <Form inline>
              <FormControl type="text" placeholder="Search" className="mr-sm-2" />
              <Button variant="outline-success">Search</Button>
            </Form>
          </Navbar.Collapse>
        </Navbar>

        <h1 id="header">Welcome to Music Accelerator!</h1>
        <div>The issue: 1. Changing just a part of the state one by one. 2. Post Method. Issue with data format imcompatibility with rest api?</div>
        {this.state.posts.map((post: any, index: number)=> {



          return <PostComponent name={post.name} image={post.image} key={index}/>

        })}

        <form className="form">
          <label>Name:</label>
          <input
            type="text"
            id="input"
            onChange={this.nameChangeHandler}
            required
          /><br /><br />
          <label>Image:</label>
          <input
            type="file"
            id="input"
            onChange={this.imageChangeHandler}
            // required
          /><br /><br />
          <label>Description:</label>
          <input
            type="text"
            id="input"
            onChange={this.descChangeHandler}
            required
          /><br /><br />
          <button id="submit" className="btn btn-success" onClick={this.postHandler}>Submit</button>
      </form>

      </div> 
    );
  }
}

export default App;
