import React from "react";
import PostComponent from "./Posts/PostComponent"
import FormComponent from "./Forms/FormComponent"
import {connect} from "react-redux";
//* (asterik) means everything in the file
import * as actionTypes from "../store/actions";


interface Props {
    id?: string;
    name?: string;
    image?: string;
    desc?: string;
    likes?: number;
}

export const HomeComponent: React.FC<Props> = (Props) => {

    return(
        <div>
            <h1 id="header">Welcome to Music Accelerator!</h1>
            <div>The issue: 1. Changing just a part of the state one by one. 2. Post Method. Issue with data format imcompatibility with rest api?</div>
                {/* posts */}
                {state.posts.map((post: any, index: number) => {
                    return <PostComponent id={post.id} name={post.name} image={post.image} desc={post.desc} likes={post.likes} key={index}/>    
                })}      

                <FormComponent/>

        {/* end of jsx */}
        </div>
    )


}

//directs how the state managed by redux will be mapped to state here. Makes data from store available to component
const mapStateToProps = (state: any) => {
    return {
        text: state.text,
        posts: state.posts
    };
  };



  //dispatch methods
  // eslint-disable-next-line 
const mapDispatchToProps = (dispatch: any) => {
    return{
        onMount: () => dispatch(actionTypes.fetchData()),
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(HomeComponent);
