import React, {useState, useEffect} from "react";
import './PostComponent_style.tsx';
import { PostWrapper } from "./PostComponent_style";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faHeart
} from "@fortawesome/free-solid-svg-icons";
import { faHeart as farHeart} from '@fortawesome/free-regular-svg-icons'
import {connect} from "react-redux";
// import { makeStyles } from '@material-ui/core/styles';
// import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
// import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
// import Typography from '@material-ui/core/Typography';
// import { red } from '@material-ui/core/colors';
// import FavoriteIcon from '@material-ui/icons/Favorite';
// import ShareIcon from '@material-ui/icons/Share';
// import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import DeleteIcon from '@material-ui/icons/Delete';

interface Props {
    id?: string;
    username?: string;
    filename?: string;
    description?: string;
    likes?: number;
    imageId?: string;
}


export const PostComponent: React.FC<Props> = (Props) => {

//Get images
    let content = null;

    if (Props.filename?.includes("jpg")){
        content = (<img className="image" src={"http://localhost:5000/posts/images/" + Props.filename} alt="is it here"></img>);
    }
    else if (Props.filename?.includes("jpeg")){
        content = (<img className="image" src={"http://localhost:5000/posts/images/" + Props.filename} alt="is it here"></img>);
    }
    else if (Props.filename?.includes("png")){
        content = (<img className="image" src={"http://localhost:5000/posts/images/" + Props.filename} alt="is it here"></img>);
    }
    else if (Props.filename?.includes("mov")){
        content = (<video className="image" src={"http://localhost:5000/posts/images/" + Props.filename} controls></video>);
    };

//Delete posts
    const deleteHandler = () => {
        axios.delete("http://localhost:5000/posts/delete/" + Props.id).then(()=>{
            console.log("image " + Props.id + " deleted");
        })

        axios.delete("http://localhost:5000/posts/image/delete/" + Props.filename);

        window.location.reload();
    };

//Add Likes

    const [likes, addLike] = useState(0);
    const [postLiked, markPost] = useState(false);

    useEffect(() => {

        let initialLikes = likes;
        //load the likes of the post
        if(initialLikes === 0){
            axios.get("http://localhost:5000/posts/" + Props.id).then((res: any) => {
                let currentLikes = res.data.likes;
                addLike(likes + currentLikes);
            })
        }
        //check if user has liked this post before
        axios.get("http://localhost:5000/post/" + Props.id).then((res: any) => {
        })
    });




    interface likeData{
        id: String | undefined,
        likes: Number
    }

    const likeHandler = (updatedLikes: number) => {

        const Likes: likeData = {
            id: Props.id,
            likes: updatedLikes,
        }

        JSON.stringify(Likes);

        axios.post("http://localhost:5000/posts/like", Likes)
        .then(()=>{console.log("like added")})

        markPost(true);

    };

    const unlikeHandler = (updatedLikes: number) => {

        const Likes: likeData = {
            id: Props.id,
            likes: updatedLikes,
        }

        JSON.stringify(Likes);

        axios.post("http://localhost:5000/posts/like", Likes)
        .then(()=>{console.log("like added")})

        markPost(false);

    }



    return( 
        <PostWrapper>

            <Card className="post_container" id={Props.id} >

                <CardHeader className="username" 
                    avatar={<Avatar aria-label="recipe" className="avatar"> R </Avatar>} 
                    action={<IconButton aria-label="settings"> <MoreVertIcon /> </IconButton>}
                    title={Props.username}/>

                <CardMedia src={"http://localhost:5000/posts/images/" + Props.filename} title="post content"/>

                {/* Image or Video */}
                {content}

                <CardContent>{Props.description}</CardContent>

                <CardActions>
                    <IconButton>
                        <FavoriteBorderIcon/>
                    </IconButton>
                    <IconButton>
                        <DeleteIcon/>
                    </IconButton>
                </CardActions>

                {/* Like Button */}
                {postLiked ? 
                <button className="likes" onClick={() => {addLike(likes - 1); unlikeHandler(likes - 1);}}><FontAwesomeIcon icon={faHeart}/> : {likes}</button>
                :
                <button className="likes" onClick={() => {addLike(likes + 1); likeHandler(likes + 1);}}><FontAwesomeIcon icon= {farHeart}/> : {likes}</button>
                }               
                {/* You use the empty function to not pass the event to the function. instead it`s just a trigger to activate the function */}
                <button className="button" onClick={deleteHandler}>X</button>
            </Card>
        </PostWrapper>
    )

}

const mapStateToProps = (state: any) => {
    return{
        posts: state.posts.posts,
    }
}

export default connect(mapStateToProps)(PostComponent);
