import React from "react";
import './PostComponent_style.tsx';
import { PostWrapper } from "./PostComponent_style";
import axios from "axios";


interface Props {
    name?: string;
    image?: string;
    desc?: string;
    likes?: number;
}

export const PostComponent: React.FC<Props> = (Props) => {

    const deleteHandler = (id: number): void => {
        axios.delete("http://127.0.0.1:8000/posts/posts/" + id)
    }


    return(
        <PostWrapper>
            <div id="container">
                <div id="name">{Props.name}</div>
                <img id="image" src={Props.image} alt="is it here"></img>
                <div id="desc">{Props.desc}</div>
                <div id="like">{Props.likes}</div>
                {/* You use the empty function to not pass the event to the function. instead it`s just a trigger to activate the function */}
                <button onClick={() => deleteHandler}></button>
            </div>
        </PostWrapper>
    )

}

export default PostComponent;
