import React, {Component} from "react"
import {connect} from "react-redux";
import PostComponent from "./Components/Posts/PostComponent"
import TestComponent from "./Components/Posts/TestComponent"
import FormComponent from "./Components/FormComponent"
import {Navbar, Nav, NavDropdown, Form, FormControl, Button} from 'react-bootstrap';
import axios from "axios";
import './App.css';
// import 'bootstrap/dist/css/bootstrap.min.css';

class App extends Component<any> {

  constructor(props: any) {
    super(props);

    // this.onChangeName = this.onChangeName.bind(this);
    // this.onChangeEmail = this.onChange.bind(this);
    // this.onChangePhone = this.onChangePhone.bind(this);
    // this.onSubmit = this.onSubmit.bind(this);

    this.state = {
        posts: [],
        input: {
          name: '',
          image: "",
          desc: ''
        }
    }
}


  state = {
    posts: [],
    input: {
    name: "",
    image: "",
    desc:""
    }
  }

  componentDidMount(){

    this.props.onMount();

    axios.get("http://127.0.0.1:8000/posts/posts/").then((response) => {
      let posts: string | number = response.data;
      //putting the content in state
      this.setState({posts: posts})
    })
  }


  render(){
    return (
      <div className="App">
        {/* navbar block */}
        <Navbar id="nav" bg="light" expand="lg">
          <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#link">Link</Nav.Link>
              <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
              </NavDropdown>
            </Nav>
            <Form inline>
              <FormControl type="text" placeholder="Search" className="mr-sm-2" />
              <Button variant="outline-success">Search</Button>
            </Form>
          </Navbar.Collapse>
        </Navbar>
        {/* mainpage content */}
        <h1 id="header">Welcome to Music Accelerator!</h1>
        <div>The issue: 1. Changing just a part of the state one by one. 2. Post Method. Issue with data format imcompatibility with rest api?</div>
        {/* posts */}
        {this.props.posts.map((post: any, index: number)=> {

          return <PostComponent name={post.name} image={post.image} desc={post.desc} likes={post.likes} key={index}/>

        })}

      <FormComponent/>

      {/* Redux Testing */}
      <TestComponent text={this.props.text}/>

      {/* end of jsx */}
      </div> 
    );
  }
}


//directs how the state managed by redux will be mapped to state here. Makes data from store available to component
const mapStateToProps = (state: any) => {
  return {
      text: state.text,
      posts: state.posts
  };
};



//dispatch methods
// eslint-disable-next-line 
const mapDispatchToProps = (dispatch: any) => {
    return{
        onIncrementCounter: () => dispatch({type: "INCREMENT", val:10 }),
        onDecrementCounter: () => dispatch({type: "DECREMENT", val: 15}),
        onMount: () => dispatch({type: "MOUNT"})
    }
}


export default connect(mapStateToProps)(App);
