import * as actionTypes from "../actions/home_actions"

const initialState = {
    posts: [],
    text: "hello",
    data: false
};


const homeReducer = (state: any = initialState, action: any) => {


    switch(action.type){
        case actionTypes.AWAIT_DATA:
            return{
              ...state,
              data: true
            };

        case actionTypes.DECREMENT:
            return{
                ///update state immutably. Don`t mutate old state, just pull old state and update
                ...state,
                text: state.text.splice(0,1)
            };

        case actionTypes.ON_MOUNT:
            return {
                //use the ... spread operator to spread and copy the current state
                ...state,
                //to update an array, use concat to 
                posts: state.posts.concat(action.array)
            };

    } 

    return state;
}


export default homeReducer
