import React, {useState} from "react";
import Modal from "react-modal";
import {connect} from "react-redux";
import {useParams} from "react-router";
import axios from "axios";
// import postsReducer from "../../store/reducers/postsReducer";
import {ProfileWrapper} from "./ProfileCompoent_style";

interface Props {
    username?: any;
    description?: string;
    posts?: any;
}


export const ProfileComponent: React.FC<Props> = (props: any) => {

    let {username} = useParams<any>();

    const [modalIsOpen, setModalState] = useState(false);
    const [userName, setUsername] = useState(username);
    const [description, setDescription] = useState("");

    const userPosts = props.posts.filter((post: any) => post.username ===  username)

    const profileData = {
        username: userName,
        description: description,
    }

    //change Profile Info

    const setProfile = () => {
        axios.post("http://localhost:5000/users/profile/" + username, profileData);
    }



    return(

    <ProfileWrapper>
        <div className="container">

            <div className="card">
                <div className="profilePic">Profile Pic</div>
                <h2 className="username">{userName}</h2>
                <div className="description"> {description}</div>
                <button className="descButton" onClick={(e) => setModalState(true)}>Edit Profile</button>
                <Modalã€€isOpen={modalIsOpen} onRequestClose={() => setModalState(false)}>
                <form className="form" action="/upload">

                {/* <label>Image:</label>
                    <input
                    type="file"
                    name="image"
                    id="input"
                    formEncType="multipart/form-data"
                    // value={filename}
                    onChange={(e: any) => {setImage(e.target.files[0]); setFilename(e.target.files[0].name); }}   
                    />  */}
                <label>Name:</label>
                    <input
                    type="text"
                    name="username"
                    id="input"
                    value={userName}
                    onChange={(e)=> setUsername(e.target.value)}
                    required
                    /><br /><br />

                <label>Description:</label>
                    <input
                    type="text"
                    name="description"
                    id="input"
                    value={description}
                    onChange={(e)=> setDescription(e.target.value)}
                    required
                    /><br /><br />


                    <button id="submit" type="submit" className="btn btn-success" onClick={(e) => {setModalState(false); setProfile();}}>Submit</button>

            </form>

                </Modal>

                <div className="posts-container">
                    {userPosts.map((post: any) => {

                        let content = null; 

                        if (post.filename?.includes("jpg")){
                                content = (<img className="image" src={"http://localhost:5000/posts/images/" + post.filename} alt="is it here"></img>);
                            }
                         else if (post.filename?.includes("jpeg")){
                                content = (<img className="image" src={"http://localhost:5000/posts/images/" + post.filename} alt="is it here"></img>);
                            }
                        else if (post.filename?.includes("png")){
                                content = (<img className="image" src={"http://localhost:5000/posts/images/" + post.filename} alt="is it here"></img>);
                            }
                        else if (post.filename?.includes("mov")){
                                 content = (<video className="image" src={"http://localhost:5000/posts/images/" + post.filename} controls></video>);
                            };    

                        return content;       
                    })}
                </div>
            </div>
        </div>
    </ProfileWrapper>

    );
};

const mapStateToProps = (state: any) => {
    return{
    posts: state.posts.posts,
    auth: state.auth.user,
    }
}

export default connect(mapStateToProps)(ProfileComponent);

//problem is that the props loaded rn are the entire posts, although i want to filter only the users posts.
