import styled from "styled-components";

export const PostWrapper = styled.div`

    .post_container {
        display:flex;
        justify-content: center;
        flex-direction: column;
        align-items: center;
        width: 1000px;
        height: 600px;
    }

    h1 {
        margin: 10px;
    }

    .image {
        width: 400px;
        height: 300px;
    }

    div {
        margin: 10px;
    }

    .button {
        display: flex; 
        margin-top: 0px;
    }
`;
