import axios from "axios";

export const AWAIT_DATA = "AWAIT_DATA";
export const DECREMENT = "DECREMENT";
export const ON_MOUNT = "ON_MOUNT";
export const TOTAL_LIKES = "TOTAL_LIKES";


export const onMount = (posts: string|number) => {
    return {
        type: ON_MOUNT,
        posts: posts,
    }
}

export const fetchPosts = () => {
    return (dispatch: any) => {
        axios.get("http://localhost:5000/posts").then((response) => {
        let posts: any = response.data;
        return posts;
        }).then((posts)=> dispatch(onMount(posts)));
    };
};

export const countTotalLikes = (username: string) => {
    return (dispatch: any) => {
        axios.get("http://localhost:5000/posts").then((response) => {

        let posts: any = response.data;

        let userPosts = posts.filter((post: any) => {
            return post.username = username;
            //what form is it returning it in?
        });

        let totalLikes = 0; 

        userPosts.forEach((post: any) => {
            totalLikes += post.likes;
            //is post.likes a number
            return totalLikes
        })

        }).then((totalLikes)=> {
            dispatch({
                type: TOTAL_LIKES,
                totalLikes: totalLikes,
            })});
    };
}

