import styled from "styled-components";

export const PostWrapper = styled.div`

    h1 {
        margin: 10px;
    }

    .container {
        display:flex;
        flex-direction: column;
        align-items: center;
    }

    #image {
        width:50%;
        height: 500px;
    }

    div {
        margin: 10px;
    }

`;
