//       console.log(response); 
//       })
//   }



export const FormComponent: React.FC<Props> = (Props) => {

    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [like, setLike] = useState("");

    const postHandler = (e: any) => {
        e.preventDefault()

        const data = {name, image, like}

        console.log(data)

        axios.post("http://127.0.0.1:8000/posts/posts/", data).then(response => {
          console.log(response); 
          })
      }


    return(

    <div className="container">
        <form className="form">
        <label>Name:</label>
        <input
          type="text"
          id="input"
          onChange={(e)=> setName(e.target.value)}
          required
        /><br /><br />
        <label>Image:</label>
        <input
          type="file"
          id="input"
          onChange={(e)=> setImage(e.target.value)}
          // required
        /><br /><br />
        <label>Description:</label>
        <input
          type="text"
          id="input"
          onChange={(e)=> setLike(e.target.value)}
          required
        /><br /><br />
        <button id="submit" className="btn btn-success" onClick={()=> postHandler}>Submit</button>
        </form>
    </div>
    )

}

export default FormComponent
