import React, {useState, useEffect} from "react";
import './PostComponent_style.tsx';
import { PostWrapper } from "./PostComponent_style";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faHeart
} from "@fortawesome/free-solid-svg-icons";
import { faHeart as farHeart} from '@fortawesome/free-regular-svg-icons'
import {connect} from "react-redux";



interface Props {
    id?: string;
    username?: string;
    filename?: string;
    description?: string;
    likes?: number;
    imageId?: string;
}


export const PostComponent: React.FC<Props> = (Props) => {

//Get images
    let content = null;

    if (Props.filename?.includes("jpg")){
        content = (<img className="image" src={"http://localhost:5000/posts/images/" + Props.filename} alt="is it here"></img>);
    }
    else if (Props.filename?.includes("jpeg")){
        content = (<img className="image" src={"http://localhost:5000/posts/images/" + Props.filename} alt="is it here"></img>);
    }
    else if (Props.filename?.includes("png")){
        content = (<img className="image" src={"http://localhost:5000/posts/images/" + Props.filename} alt="is it here"></img>);
    }
    else if (Props.filename?.includes("mov")){
        content = (<video className="image" src={"http://localhost:5000/posts/images/" + Props.filename} controls></video>);
    };

//Delete posts
    const deleteHandler = () => {
        axios.delete("http://localhost:5000/posts/delete/" + Props.id).then(()=>{
            console.log("image " + Props.id + " deleted");
        })

        axios.delete("http://localhost:5000/posts/image/delete/" + Props.filename);

        window.location.reload();
    };

//Add Likes

    const [likes, addLike] = useState(0);
    const [postLiked, markPost] = useState(false);

    useEffect(() => {
        axios.get("http://localhost:5000/posts/" + Props.id).then((res: any) => {
            console.log(res.data.likes);

            addLike(likes + res.data.likes);
        })

    }, [likes, Props.id]);

    interface likeData{
        id: String | undefined,
        likes: Number
    }

    const likeHandler = (updatedLikes: number) => {

        const Likes: likeData = {
            id: Props.id,
            likes: updatedLikes,
        }

        JSON.stringify(Likes);

        axios.post("http://localhost:5000/posts/like", Likes)
        .then(()=>{console.log("like added")})

        markPost(true);

    };

    const unlikeHandler = (updatedLikes: number) => {

        const Likes: likeData = {
            id: Props.id,
            likes: updatedLikes,
        }

        JSON.stringify(Likes);

        axios.post("http://localhost:5000/posts/like", Likes)
        .then(()=>{console.log("like added")})

        markPost(false);

    }



    return( 
        <PostWrapper>
            <div className="post_container" id={Props.id} >
                <div className="username">{Props.username}</div>
                {/* <img className="image" src={"http://localhost:5000/posts/images/" + Props.filename} alt="is it here"></img>
                <video className="image" src={"http://localhost:5000/posts/images/" + Props.filename} controls></video> */}
                {content}
                <div className="description">{Props.description}</div>
                {postLiked ? 
                <button className="likes" onClick={() => {addLike(likes - 1); unlikeHandler(likes - 1);}}><FontAwesomeIcon icon={faHeart}/> : {likes}</button>
                :
                <button className="likes" onClick={() => {addLike(likes + 1); likeHandler(likes + 1);}}><FontAwesomeIcon icon= {farHeart}/> : {likes}</button>
                }               
                {/* You use the empty function to not pass the event to the function. instead it`s just a trigger to activate the function */}
                <button className="button" onClick={deleteHandler}>X</button>
            </div>
        </PostWrapper>
    )

}

const mapStateToProps = (state: any) => {
    return{
        posts: state.posts.posts,
    }
}

export default connect(mapStateToProps)(PostComponent);
